<script >
import router from "@/router";
import axios from 'axios';
// import { useAppStore } from '@/stores/index';

export default {
  name: 'Login',
  data() {
      return {
        email: "",
        password: "",
        auth: {},
        loading: false,
        loadingForgot: false,
        errors: {email: "", password: ""},
        errorsReset: {emailReset: "", passwordReset: "",token: ""},
        invalid: "",
        showForgot: true,
        showReset: false,
        forgotEmail: "",
        forgotPassword: "",
        forgotPasswordConfirm: "",
        emailError: "",
        token: "",
        // store: useAppStore()
      };
  },
  methods: {
    async login(e) {
      e.preventDefault();
      this.errors = {};
      this.invalid = "";
      axios.post("/api/authenticate", { email: this.email, password: this.password })
        .then(async (res) => {
          if (res.status === 200) {
            console.log(res);
            localStorage.setItem("access_token", res.data.token);
            // this.$store.commit("login", res.data.user);
            this.auth = res.data.user;
            // this.store.isAuth()
            this.loading = true;

            // Use await with router.push to ensure the navigation occurs after the token is stored
            if(res.data.token){
              await router.push({ path: "/auth/home" });
            }else{
              this.emailError ='Email or Passwort not correct' 
            }
            
          }})
        .catch((error) => {
          console.log(error);
          if (error.response.status && error.response.status === 401) {
            this.invalid = error.response.data;
            this.loading = false;
          
          } else if (error.response.status && error.response.status === 422) {
            this.errors = error.response.data.errors;
            this.loading = false;
          
          }

        
          
        });
    },
    async forgot(e) {
      this.loadingForgot = true;
      this.emailError = ""
      e.preventDefault();
      axios.post("/api/forgot-password", { email: this.forgotEmail})
        .then(res => {
          if (res.status == 200) {
            this.showReset = true
          }
        })
        .catch(err => {
            console.log(err);
            this.emailError = "email address is incorrect"
            this.loadingForgot = false;
        })
    },
    async reset(e) {
      this.errorsReset= {email: "", password: "",token: ""}
      e.preventDefault();
      axios.post("/api/reset-password", { password: this.forgotPassword,password_confirmation: this.forgotPasswordConfirm,token: this.token,email: this.forgotEmail})
        .then(res => {
          console.log(res);
          if (res.status == 200) {
            this.showReset = false
            this.toggleForgotModel()
          }
        })
        .catch(err => {
            console.log(err);
            if (err.response.status === 422) {
              this.errorsReset =  err.response.data.errors;
              this.loading = false;
          }
        })
    },
    //forget password
    toggleForgotModel() {
      this.showForgot = !this.showForgot;
      console.log(this.showForgot)
    },
    closeResetModel() {
      this.showReset = false
    },
    
  }
  
}
  
</script>
